mapper.select:
SELECT COUNT(*)
FROM ESTIMATE 
WHERE MEM_ID = #{memId}
  AND EST_ADDRESS = #{estAddress}
  AND est_submit = 'N'

mapper.update:
UPDATE ESTIMATE
SET 
    EST_SDATE = #{estSdate},
    DONG_NM = #{dongNm}, 
    HO_NM = #{hoNm}
WHERE MEM_ID = #{memId}
AND EST_ADDRESS = #{estAddress}

UPDATE ESTIMATE
SET 
    est_interior_desc = #{estInteriorDesc}
WHERE MEM_ID = #{memId}
AND est_id = #{estId}

UPDATE ESTIMATE
SET 
    est_submit = 'Y',
    est_area = #{estArea}
WHERE 1=1 
AND est_id = #{estId}
