mapper.update:
UPDATE members
SET
	MEM_NAME = #{memName}
	,MEM_PW = #{memPw}
	,MEM_ADDRESS = #{memAddress}
	,MEM_PHONE = #{memPhone}
	,MEM_NICK = #{memNick}
	,MEM_TYPE = #{memType}
	,MEM_IMG = #{memImg}
	,COM_CEO = #{comCeo}
	,COM_BRC = #{comBrc}
	,COM_MA_NM = #{comMaNm}
	,COM_MA_PN = #{comMaPn}
	,MEM_HP = #{memHp}
	,mem_email = #{memEmail}
WHERE 1=1
	AND MEM_ID = #{memId}
